import styled from 'styled-components'

export const CardsContainer = styled.div`
    display:flex;
    flex-direction:row;
    justify-content:space-between;
    width: 70%;
    margin: auto;
    padding: 2em;
    background-color: #f6318c;
    border-radius: 15px;
`;
